import React, { Component } from "react";
import Slider from "react-slick"; 
import { FaArrowRight, FaArrowLeft } from "react-icons/fa"; // Import icon

class Cart extends Component {
  render() {
    const items = [
      // Danh sách sản phẩm
    ];

    const settings = {
      dots: false,
      infinite: true,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 1,
      nextArrow: (
        <div className="custom-arrow custom-arrow-right">
          <FaArrowRight />
        </div>
      ),
      prevArrow: (
        <div className="custom-arrow custom-arrow-left">
          <FaArrowLeft />
        </div>
      ),
    };

    return (
      <div className="bg-white">
        <div className="mx-auto max-w-2xl px-4 py-16 sm:px-6 sm:py-24 lg:max-w-7xl lg:px-8">
          <h2 className="text-2xl font-bold tracking-tight text-gray-900">Bất ngờ mỗi tuần</h2>
          <Slider {...settings}>
            {items.map((item, index) => (
              <div className="group relative" key={index}>
                {/* Hiển thị sản phẩm */}
              </div>
            ))}
          </Slider>
        </div>
      </div>
    );
  }
}

export default Cart;
