import React, { useState } from "react";

const SubItem = ({ title }) => (
  <div className="ml-4 mt-1 text-sm text-gray-600">{title}</div>
);

const Item = ({ title, subItems }) => {
  const [isOpen, setIsOpen] = useState(false); // State để quản lý trạng thái hiển thị

  const toggleOpen = () => {
    setIsOpen(!isOpen); // Chuyển đổi trạng thái
  };

  return (
    <div className="mb-4 border-b">
      <div className="flex justify-between items-center py-2">
        <h3 className="text-lg">{title}</h3>
        <button onClick={toggleOpen}>
          {isOpen ? "▼" : ">"}
        </button>
      </div>
      {isOpen && (
        <div>
          {subItems.map((subItem, index) => (
            <SubItem key={index} title={subItem} />
          ))}
        </div>
      )}
    </div>
  );
};

const App = () => {
  const items = [
    {
      title: "Item 1",
      subItems: ["Sub Item 1.1", "Sub Item 1.2", "Sub Item 1.3"],
    },
    {
      title: "Item 2",
      subItems: ["Sub Item 2.1", "Sub Item 2.2"],
    },
    {
      title: "Item 3",
      subItems: ["Sub Item 3.1"],
    },
  ];

  return (
    <div className="p-4">
      {items.map((item, index) => (
        <Item key={index} title={item.title} subItems={item.subItems} />
      ))}
    </div>
  );
};

export default App;
